mapper.select:
select
id, username, password, nick_name, age, sex, address
from user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and nick_name = #{nickName}


        and age = #{age}


        and sex = #{sex}


        and address = #{address}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and nick_name = #{nickName}


        and age = #{age}


        and sex = #{sex}


        and address = #{address}
